package assignment;

public class OnetoFour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		{
			for(int i=1;i<=4;i++)
			{
				System.out.println(i);
			}
			System.out.print(1);
			System.out.print(" "+2);
			System.out.print(" "+3);
			System.out.print(" "+4);
			System.out.println("\n");
			
			for(int j=1;j<=4;j++)
			{
			System.out.printf("%d\t",j);
			}
			
		}

	}

}


